import "./index.scss";
import React from "react";
function App() {
	const [counter, setCounter] = React.useState(0);

	const onClickPlus = () => {
		setCounter(counter + 1);
	};

	const onClickMinus = () => {
		setCounter(counter - 1);
	};

	return (
		<div className="App">
			<div>
				<h2>Счетчик:</h2>
				<h1>0</h1>
				<button onClick={onClickPlus} className="minus">
					- Минус
				</button>
				<button onClick={onClickMinus} className="plus">
					Плюс +
				</button>
			</div>
		</div>
	);
}

export default App;
