import React from 'react';
import './index.scss';
import { Collection } from './Collection';

function App() {

	const [collections, setCollections] = React.useState([]);

	React.useEffect(() => {
		fetch('https://63918995ac688bbe4c4afe08.mockapi.io/photos')
			.then((res) => res.json())
			.then(json => {
				setCollections(json);
			})
			.catch(err => {
				console.log(err);
				alert("Downloading photos failed");
			});
	}, []);

	return (
		<div className="App">
			<h1>Моя коллекция фотографий</h1>
			<div className="top">
				<ul className="tags">
					<li className="active">Все</li>
					<li>Горы</li>
					<li>Море</li>
					<li>Архитектура</li>
					<li>Города</li>
				</ul>
				<input className="search-input" placeholder="Поиск по названию" />
			</div>
			<div className="content">
				{
					collections.map(obj => (
						<Collection key={obj.category} name={obj.name} images={obj.photos} />
					))
				}
			</div>
			<ul className="pagination">
				<li>1</li>
				<li className="active">2</li>
				<li>3</li>
			</ul>
		</div>
	);
}

export default App;
